import { useState } from "react";

const MovieForm = ({ setMovieState }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [posterURL, setPosterURL] = useState("");
  const [rating, setRating] = useState("");

  const handclick = () => {
    const movies = [
      {
        id: 1,
        title: "Inception",
        Description:
          "In a world where technology allows people to enter the dreams of others, a thief uses this power for corporate espionage but soon finds himself in a complex heist that involves planting an idea into someone's mind",
        PosterURL: "Inception Poster",
        Rating: "8.8/10",
      },
    ];
  };

  return (
    <>
      <div>
        <input
          type="text"
          placeholder="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>

      <div>
        <input
          type="text"
          placeholder="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="posterUrl"
          value={posterURL}
          onChange={(e) => setPosterURL(e.target.value)}
        />
      </div>

      <div>
        <input
          type="text"
          placeholder="rating"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
        />
      </div>

      <button>ADD</button>
    </>
  );
};

export default MovieForm;
